import React, { useState, useEffect } from "react";


// mysql Ïó∞Îèô Í≥ºÏ†ï

import "../styles/App.css";
import HomeImage from "../assets/home.png";
import TodayImage from "../assets/today.png";
import CHImage from "../assets/ch.png";
import RingImage from "../assets/ring.png";
import ComImage from "../assets/com.png";
// import Ïù¥ÎØ∏ÏßÄ Îì§

function Com() {
  const [message, setMessage] = useState([]);
  useEffect(() => {
    fetch("/hello")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setMessage(data);
      });
  }, []);

  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'space-between',
    height: '100vh',
    flexGrow: 1,
  };

  const contentWrapperStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'space-between',
    width: '100%',
    height: 'auto',
    background: '#F7D358',
    padding: '40px 0 120px 0',
  };

  const centerWhiteStyle = {
    width: '80%',
    height: 'auto',
    background: 'white',
    display: 'flex',
    margin: '0 auto',
    justifyContent: 'space-between',
    flexDirection: 'column',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.2)',
  };

  const homeWrapperStyle = {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '10px',
  };

  const homeImageStyle = {
    width: '45px',
    height: '45px',
  };

  const homeTextStyle = {
    fontSize: '22px',
    fontWeight: 'bold',
    textAlign: 'left',
    marginLeft: '4px',
  };

  const tabImageStyle = {
    width: '35px',
    height: '35px',
    margin: '7px',
    padding: '5px 20px 20px 20px',
  };

  const tabtext = {
    fontSize: '15px',
    fontWeight: 'bold',
    textAlign: 'center',
    margin: '-25px 0 3px 0',
  };

  const bottomTabStyle = {
    width: '100%',
    height: 'auto',
    background: 'white',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '4px',
    position: 'fixed',
    bottom: '0',
    zIndex: '1',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.2)',
  };

  const appBackgroundStyle = {
    width: '90%',
    height: 'auto',
    background: 'white',
    border: '1px solid #ddd',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'flex-start',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    zIndex: '0',
    position: 'relative',
    margin: '10px',
  };

  const likeButtonStyle = {
    cursor: 'pointer',
    color: 'gray',
    alignSelf: 'flex-start',
    marginTop: '10px',
  };

  const writeButtonStyle = {
    cursor: 'pointer',
    color: 'blue',
    alignSelf: 'flex-start',
    marginTop: '10px',
  };

  const [likes, setLikes] = useState(Array(8).fill(false));

  const handleLikeClick = (index) => {
    const updatedLikes = [...likes];
    updatedLikes[index] = !updatedLikes[index];
    setLikes(updatedLikes);
  };

  const handleWriteButtonClick = () => {
    window.location.href = './write'; // Ïù¥ÎèôÌï† ÌéòÏù¥ÏßÄ Í≤ΩÎ°úÏóê ÎßûÍ≤å ÏàòÏ†ï
  };

  const handleHomeImageClick = () => {
    window.location.href = "./today";
  };

  const handleTodayImageClick = () => {
    window.location.href = "./today";
  };

  const handleCHImageClick = () => {
    window.location.href = "./ch";
  };

  const handleRingImageClick = () => {
    window.location.href = "/ring";
  };

  const handleComImageClick = () => {
    window.location.href = "/com";
  };

  return (
    <div className="App" style={containerStyle}>
       <ul>
        {Array.isArray(message) ? (
          message.map((v, idx) => <li key={`${idx}-${v}`}>{v}</li>)
        ) : (
          <li>ÏÑúÎ≤ÑÏó∞Îèô ÏÑ±Í≥µ </li>
        )}
      </ul>
      <div style={contentWrapperStyle}>
        <div style={centerWhiteStyle}>
          <div style={homeWrapperStyle}>
            <img
              src={HomeImage}
              alt="home"
              style={{ ...homeImageStyle }}
              onClick={handleHomeImageClick}
            />
            <span style={homeTextStyle}>Ïª§ÎÆ§ÎãàÌã∞</span>
          </div>

          <div style={{ justifyContent: 'center', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            {[...Array(8)].map((_, index) => (
              <div key={index} style={appBackgroundStyle}>
                <p style={{ textAlign: 'left' }}>ÎÇ¥Ïö© {index + 1}</p>
                <span
                  style={likeButtonStyle}
                  onClick={() => handleLikeClick(index)}
                >
                  {likes[index] ? '‚ù§Ô∏è Ï¢ãÏïÑÏöî Ï∑®ÏÜå' : 'ü§ç Ï¢ãÏïÑÏöî'}
                  </span>
              </div>
            ))}
          </div>
        </div>
      </div>
     <button style={{...writeButtonStyle}}
     onClick={handleWriteButtonClick}/>
      <div style={bottomTabStyle}>
        <div>
          <img
            src={TodayImage}
            alt="Today"
            style={tabImageStyle}
            onClick={handleTodayImageClick}
          />
          <p style={tabtext}>Ìà¨Îç∞Ïù¥</p>
        </div>
        <div>
          <img
            src={CHImage}
            alt="CH"
            style={tabImageStyle}
            onClick={handleCHImageClick}
          />
          <p style={tabtext}>Ï∂îÏ≤ú</p>
        </div>
        <div>
          <img
            src={RingImage}
            alt="Ring"
            style={tabImageStyle}
            onClick={handleRingImageClick}
          />
          <p style={tabtext}>ÏïåÎ¶º</p>
        </div>
        <div>
          <img
            src={ComImage}
            alt="Com"
            style={tabImageStyle}
            onClick={handleComImageClick}
          />
          <p style={tabtext}>ÏÜåÌÜµ</p>
        </div>
      </div>
    </div>
  );
}

export default Com;
